mapper.update:
UPDATE Member
   SET name = #{name}


    AND position_id = #{portionId}

WHERE member_id = #{memberId}

mapper.delete:
UPDATE Member
  SET del_yn = 'Y'
WHERE department_id = #{departmentId} and member_id = #{memberId}

mapper.update:
UPDATE Member
   SET department_id = #{nextDepartmentId}
 WHERE department_id = #{departmentId}
   AND member_id = #{memberId}
